import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);
  const [newItemName, setNewItemName] = useState('');
  const [newItemType, setNewItemType] = useState('');

  useEffect(() => {
    // Fetch items from the backend API
    axios.get('/api/items')
      .then((response) => {
        setItems(response.data);
      })
      .catch((error) => {
        console.error('Error fetching items:', error);
      });
  }, []);

  const handleAddItem = () => {
    // Add a new item to the backend API
    axios.post('localhost:27017/api/items', { name: newItemName, type: newItemType })
      .then((response) => {
        setItems([...items, response.data]);
        setNewItemName('');
        setNewItemType('');
      })
      .catch((error) => {
        console.error('Error adding item:', error);
      });
  };

  const handleDeleteItem = (itemId) => {
    // Delete an item from the backend API
    axios.delete(`/api/items/${itemId}`)
      .then(() => {
        setItems(items.filter((item) => item._id !== itemId));
      })
      .catch((error) => {
        console.error('Error deleting item:', error);
      });
  };

  return (
    <div>
      <h1>Item List</h1>
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            {item.name} - {item.type}
            {/* Show delete button only for 'retail manager' */}
            {true && (
              <button onClick={() => handleDeleteItem(item._id)}>Delete</button>
            )}
          </li>
        ))}
      </ul>
      {/* Show add item form only for 'retail manager' */}
      {true && (
        <div>
          <h2>Add Item</h2>
          <input type="text" value={newItemName} onChange={(e) => setNewItemName(e.target.value)} placeholder="Item Name" />
          <input type="text" value={newItemType} onChange={(e) => setNewItemType(e.target.value)} placeholder="Item Type" />
          <button onClick={handleAddItem}>Add Item</button>
        </div>
      )}
    </div>
  );
}

export default App;
